import React, { useState, useMemo } from 'react';

// build many models per brand
const makeRange = (prefix, count, start = 1) => Array.from({ length: count }, (_, i) => `${prefix} ${i + start}`);

const brands = [
  { name: 'Samsung', ar: 'سامسونج', models: [...makeRange('Galaxy S', 30), ...makeRange('Galaxy A', 20), ...makeRange('Galaxy Note', 8)] },
  { name: 'Xiaomi', ar: 'شاومي', models: [...makeRange('Redmi Note', 20), ...makeRange('Mi', 12), ...makeRange('Poco', 12)] },
  { name: 'Oppo', ar: 'اوبو', models: [...makeRange('Oppo Reno', 16), ...makeRange('Oppo A', 14)] },
  { name: 'Huawei', ar: 'هواوي', models: [...makeRange('Huawei P', 16), ...makeRange('Huawei Mate', 10)] },
];

function DeviceSelector({ lang = 'ar', onSelect, selectedDevice }) {
  const [selectedId, setSelectedId] = useState(selectedDevice?.id || '');

  const allOptions = useMemo(() => {
    return brands.flatMap(b => b.models.map((m, idx) => ({ id: `${b.name.toLowerCase()}-${idx + 1}`, model: m, brand: b.name, brandAr: b.ar })));
  }, []);

  const handleChange = (e) => {
    const id = e.target.value;
    setSelectedId(id);
    const dev = allOptions.find(d => d.id === id) || null;
    onSelect && onSelect(dev);
  };

  return (
    <div style={{ margin: '12px 0' }}>
      <label style={{ display: 'block', marginBottom: 6, color: '#fff', fontWeight: 800 }}>{lang === 'ar' ? 'اختر موديل الجهاز' : 'Select device model'}</label>
      <select value={selectedId} onChange={handleChange} style={{ width: '100%', padding: '12px 14px', borderRadius: 12, background: 'rgba(255,255,255,0.02)', color: '#fff', border: '1px solid rgba(255,255,255,0.06)', fontWeight:700 }}>
        <option value="">{lang === 'ar' ? 'اختر الموديل' : 'Select model'}</option>
        {brands.map((b) => (
          <optgroup key={b.name} label={lang === 'ar' ? b.ar : b.name} style={{ color: '#fff' }}>
            {b.models.map((m, idx) => (
              <option key={m + idx} value={`${b.name.toLowerCase()}-${idx + 1}`}>{m}</option>
            ))}
          </optgroup>
        ))}
      </select>
    </div>
  );
}

export default DeviceSelector;
