import React, { useState, useEffect } from 'react';
import DeviceSelector from './components/DeviceSelector';
import AndroidVersionSelector from './components/AndroidVersionSelector';
import AuthPopup from './components/AuthPopup';
import SubscriptionPopup from './components/SubscriptionPopup';
import './index.css';

const steps = [
  { title: { ar: 'ابدأ التشخيص الذكي', en: 'Start Smart Diagnosis' }, desc: { ar: 'اختر جهازك واصدار الأندرويد والمشكلة', en: 'Select your device, Android version, and issue' } },
];

function App() {
  const [lang, setLang] = useState('ar');
  const [user, setUser] = useState(() => {
    try { return JSON.parse(localStorage.getItem('user')); } catch { return null; }
  });
  const [showAuth, setShowAuth] = useState(false);
  const [showSub, setShowSub] = useState(false);
  const [selectedDevice, setSelectedDevice] = useState(null);
  const [androidVersion, setAndroidVersion] = useState('');
  const [problemText, setProblemText] = useState('');
  const [aiReply, setAiReply] = useState('');
  const [loadingAI, setLoadingAI] = useState(false);

  useEffect(() => {
    // keep user in localStorage
    if (user) localStorage.setItem('user', JSON.stringify(user));
    else localStorage.removeItem('user');
  }, [user]);

  const handleAuthClose = (u) => {
    setShowAuth(false);
    if (u) setUser(u);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setShowAuth(true);
  };

  // New: send problem to backend /api/ask-ai which proxies to Gemini when configured
  const handleAskAI = async () => {
    setAiReply('');
    if (!problemText.trim()) { setAiReply('من فضلك اكتب المشكلة'); return; }
    // optional: require device/version
    if (!selectedDevice || !androidVersion) {
      setAiReply('الرجاء اختيار نوع الجهاز وإصدار الأندرويد أولاً');
      return;
    }

    setLoadingAI(true);
    try {
      const res = await fetch('/api/ask-ai', {
        method: 'POST', headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: problemText.trim(), device: selectedDevice.model, androidVersion })
      });
      if (!res.ok) {
        const err = await res.json().catch(() => ({}));
        throw new Error(err.error || `Server ${res.status}`);
      }
      const data = await res.json();
      setAiReply(data.reply || data.answer || 'لم يصل رد من الذكاء الاصطناعي');
    } catch (err) {
      console.error('ask-ai error', err);
      setAiReply('تعذر الاتصال بخدمة الذكاء الاصطناعي. تأكد من تشغيل backend وأن GEN_API_KEY مضبوط إذا أردت استخدام Gemini الحقيقي.');
    } finally {
      setLoadingAI(false);
    }
  };

  return (
    <div className="gradient-bg container">
      <header className="header header-row">
        <div className="header-left">
          <button className="btn" onClick={() => setShowAuth(true)}>{user ? (user.name || user.email) : 'تسجيل/دخول'}</button>
        </div>

        <div className="site-title neon">MaroPhone-AI</div>

        <div className="header-right">
          <button className="btn" onClick={() => setShowSub(true)}>اشترك الآن</button>
        </div>
      </header>

      <main className="app-shell">
        <section className="panel">
          <h2 style={{marginTop:0}}>{steps[0].title[lang]}</h2>
          <p style={{opacity:0.9}}>{steps[0].desc[lang]}</p>

          <DeviceSelector lang={lang} onSelect={setSelectedDevice} selectedDevice={selectedDevice} />
          <AndroidVersionSelector lang={lang} value={androidVersion} onChange={setAndroidVersion} />

          <textarea className="problem-textarea" placeholder={lang === 'ar' ? 'اكتب وصف المشكلة هنا...' : 'Describe your problem...'} value={problemText} onChange={e => setProblemText(e.target.value)} />

          <div className="actions-row">
            <button className="btn primary" onClick={handleAskAI} disabled={loadingAI}>{loadingAI ? 'جاري الاتصال...' : (lang === 'ar' ? 'اسأل الذكاء الاصطناعي' : 'Ask AI')}</button>
            <button className="btn" onClick={() => setProblemText('')} style={{opacity:0.85}}>مسح</button>
          </div>

          {/* AI reply box */}
          {aiReply && (
            <div className="ai-reply modern-reply" style={{background:'#ffffff',color:'#0b1220',marginTop:18,borderRadius:12,padding:16}}>
              <h3 style={{marginTop:0}}>{lang === 'ar' ? 'رد الذكاء الاصطناعي' : 'AI Response'}</h3>
              <div style={{whiteSpace:'pre-wrap'}}>{aiReply}</div>
            </div>
          )}
        </section>
      </main>

      {showAuth && <AuthPopup lang={lang} onClose={handleAuthClose} />}
      {showSub && <SubscriptionPopup lang={lang} onClose={() => setShowSub(false)} />}
    </div>
  );
}

export default App;
