import React, { useState } from 'react';

const plans = [
  {
    price: 50,
    title: 'باقة أساسية',
    features: [
      'رفع صورة واحدة للمشكلة شهرياً',
      'دعم فني ذكي للنص والصورة',
      'أولوية في الردود',
    ],
    payUrl: 'https://pay.example.com/basic',
  },
  {
    price: 100,
    title: 'باقة متقدمة',
    features: [
      'رفع حتى 5 صور شهرياً',
      'تحليل متقدم للصور',
      'دعم فني مباشر',
      'أولوية عالية في الردود',
    ],
    payUrl: 'https://pay.example.com/pro',
  },
  {
    price: 150,
    title: 'باقة VIP',
    features: [
      'رفع صور غير محدود شهرياً',
      'تحليل فوري للصور',
      'دعم فني خاص',
      'استشارات هاتفية',
      'أولوية قصوى في الردود',
    ],
    payUrl: 'https://pay.example.com/vip',
  },
];

function SubscriptionPopup({ lang, onClose }) {
  return (
    <div className="popup">
      <div className="popup-content">
        <button style={{ float: 'right', background: 'none', border: 'none', fontSize: 22, cursor: 'pointer' }} onClick={onClose}>×</button>
        <h2 style={{ fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto', marginBottom: 16 }}>
          {lang === 'ar' ? 'اختر الباقة المناسبة' : 'Choose Your Plan'}
        </h2>
        <div style={{ display: 'flex', flexDirection: 'column', gap: 18 }}>
          {plans.map((plan, i) => (
            <div key={i} className="card" style={{ background: '#f7f9fc', border: '1px solid #eee' }}>
              <h3 style={{ color: 'var(--primary)', marginBottom: 8 }}>{plan.title} - {plan.price} جنيه/شهر</h3>
              <ul style={{ margin: '0 0 12px 0', padding: '0 0 0 18px' }}>
                {plan.features.map((f, j) => <li key={j}>{f}</li>)}
              </ul>
              <button className="btn-primary" onClick={() => window.open(plan.payUrl, '_blank')}>
                {lang === 'ar' ? 'ادفع الآن' : 'Pay Now'}
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SubscriptionPopup;
