import React, { useState, useMemo } from 'react';

const brands = [
  { name: 'Samsung', ar: 'سامسونج', devices: Array.from({ length: 40 }, (_, i) => ({ id: `samsung-${i + 1}`, model: `Galaxy S${i + 1}`, imei: '', serial: '' })) },
  { name: 'Xiaomi', ar: 'شاومي', devices: Array.from({ length: 30 }, (_, i) => ({ id: `xiaomi-${i + 1}`, model: `Redmi ${i + 1} Pro`, imei: '', serial: '' })) },
  { name: 'Oppo', ar: 'اوبو', devices: Array.from({ length: 20 }, (_, i) => ({ id: `oppo-${i + 1}`, model: `Oppo Reno${i + 1}`, imei: '', serial: '' })) },
  { name: 'Huawei', ar: 'هواوي', devices: Array.from({ length: 20 }, (_, i) => ({ id: `huawei-${i + 1}`, model: `Huawei P${i + 1}`, imei: '', serial: '' })) },
];

function DeviceSelector({ lang = 'ar', onSelect, selectedDevice }) {
  const [search, setSearch] = useState('');
  const [selectedId, setSelectedId] = useState(selectedDevice?.id || null);

  const allDevices = useMemo(() => brands.flatMap(b => b.devices.map(d => ({ ...d, brand: lang === 'ar' ? b.ar : b.name }))), [lang]);
  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return allDevices;
    return allDevices.filter(d => d.model.toLowerCase().includes(q) || (d.imei && d.imei.includes(q)) || (d.serial && d.serial.includes(q)));
  }, [allDevices, search]);

  const handleSelect = (device) => {
    // set local UI state
    setSelectedId(device.id);
    // inform parent
    onSelect && onSelect(device);
  };

  return (
    <div className="device-selector">
      <input type="text" placeholder={lang === 'ar' ? 'ابحث عن الموديل أو imei' : 'Search model or imei'} value={search} onChange={e => setSearch(e.target.value)} style={{width:'100%',padding:10,borderRadius:10,border:'1px solid rgba(255,255,255,0.04)',background:'transparent',color:'inherit'}} />
      <div style={{ maxHeight: 320, overflow: 'auto', marginTop: 10 }}>
        {filtered.map(d => (
          <div key={d.id} className={`device-item ${selectedId === d.id ? 'selected' : ''}`} onClick={() => handleSelect(d)} style={{display:'flex',justifyContent:'space-between',padding:10,cursor:'pointer'}}>
            <div>
              <div style={{fontWeight:700}}>{d.model}</div>
              <div style={{fontSize:12,opacity:0.8}}>{d.brand}</div>
            </div>
            <div style={{alignSelf:'center'}}>{selectedId === d.id ? <span style={{color:'var(--accent)'}}>محدد</span> : <span style={{opacity:0.6}}>اختر</span>}</div>
          </div>
        ))}
        {filtered.length === 0 && <div style={{padding:8,opacity:0.7}}>لا توجد نتائج</div>}
      </div>
    </div>
  );
}

export default DeviceSelector;
