import React from 'react';
import { FaCrown, FaStar, FaRocket } from 'react-icons/fa';

const plans = [
  {
    price: 50,
    title: 'Basic',
    icon: <FaStar style={{ color: '#ffb300', fontSize: 28 }} />,
    features: [
      'رفع صورة واحدة للمشكلة شهرياً',
      'دعم فني ذكي للنص والصورة',
      'أولوية في الردود',
    ],
    payUrl: 'https://paymob.xyz/yatWnT9T/',
  },
  {
    price: 100,
    title: 'Pro',
    icon: <FaRocket style={{ color: '#2a5298', fontSize: 28 }} />,
    features: [
      'رفع حتى 5 صور شهرياً',
      'تحليل متقدم للصور',
      'دعم فني مباشر',
      'أولوية عالية في الردود',
    ],
    payUrl: 'https://pay.example.com/pro',
  },
  {
    price: 150,
    title: 'VIP',
    icon: <FaCrown style={{ color: '#ffb300', fontSize: 32 }} />,
    features: [
      'رفع صور غير محدود شهرياً',
      'تحليل فوري للصور',
      'دعم فني خاص',
      'استشارات هاتفية',
      'أولوية قصوى في الردود',
    ],
    payUrl: 'https://pay.example.com/vip',
  },
];

function SubscriptionPopup({ lang, onClose }) {
  return (
    <div className="popup">
      <div className="popup-content" style={{ minWidth: 340, background: 'linear-gradient(120deg,#fffbe7 0%,#e3eaff 100%)', boxShadow: '0 8px 32px #2a529822' }}>
        <button style={{ float: 'right', background: 'none', border: 'none', fontSize: 22, cursor: 'pointer' }} onClick={onClose}>×</button>
        <h2 style={{ fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto', marginBottom: 16, color: 'var(--primary)', fontWeight: 900 }}>
          {lang === 'ar' ? 'اختر الباقة المناسبة' : 'Choose Your Plan'}
        </h2>
        <div style={{ display: 'flex', flexDirection: 'row', gap: 18, overflowX: 'auto' }}>
          {plans.map((plan, i) => (
            <div key={i} className="card animated-card" style={{ background: '#fff', border: '1px solid #eee', minWidth: 220, boxShadow: '0 4px 16px #2a529822', transition: 'transform 0.2s', marginBottom: 8, borderRadius: 18, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
              <div style={{ marginBottom: 8 }}>{plan.icon}</div>
              <h3 style={{ color: 'var(--primary)', marginBottom: 8, fontWeight: 800 }}>{plan.title} - {plan.price} جنيه/شهر</h3>
              <ul style={{ margin: '0 0 12px 0', padding: '0 0 0 18px', textAlign: 'left' }}>
                {plan.features.map((f, j) => <li key={j} style={{ marginBottom: 4 }}>{f}</li>)}
              </ul>
              <button className="btn-primary" style={{ marginTop: 8, borderRadius: 12, fontWeight: 700 }} onClick={() => window.open(plan.payUrl, '_blank')}>
                {lang === 'ar' ? 'ادفع الآن' : 'Pay Now'}
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SubscriptionPopup;
