import React, { useState } from 'react';
import DeviceSelector from './components/DeviceSelector';
import AuthPopup from './components/AuthPopup';
import SubscriptionPopup from './components/SubscriptionPopup';

async function getGeminiReply(problemText) {
  const apiKey = 'AIzaSyC4M2gQOe6sPlcoZRPu8g38VWbvfKAXSsw';
  const url = `https://generativelanguage.googleapis.com/v1beta2/models/gemini-pro:generateContent?key=${apiKey}`;
  const res = await fetch(url, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      contents: [{ parts: [{ text: problemText }] }]
    })
  });
  const data = await res.json();
  return data.candidates?.[0]?.content?.parts?.[0]?.text || '';
}

const steps = [
  { title: { ar: 'ŸÖÿ±ÿ≠ÿ®ÿß ÿ®ŸÉ', en: 'Welcome' }, desc: { ar: 'ÿßÿ®ÿØÿ£ ÿßŸÑÿ™ÿ¥ÿÆŸäÿµ ÿßŸÑÿ∞ŸÉŸä ŸÑŸÖÿ¥ÿßŸÉŸÑ ÿ¨Ÿáÿßÿ≤ŸÉ', en: 'Start smart diagnosis for your device' } },
  { title: { ar: 'ÿßÿÆÿ™ÿ± ŸÜŸàÿπ ÿßŸÑÿ¨Ÿáÿßÿ≤', en: 'Select Device Type' }, desc: { ar: 'ÿ≠ÿØÿØ ŸÜŸàÿπ ÿ¨Ÿáÿßÿ≤ŸÉ ŸàÿßŸÑŸÖÿ¥ŸÉŸÑÿ©', en: 'Choose your device and issue' } },
  { title: { ar: 'ÿßŸÑÿ≠ŸÑ ÿßŸÑŸÖŸÇÿ™ÿ±ÿ≠', en: 'Suggested Solution' }, desc: { ar: 'ÿ¥ÿßŸáÿØ ÿßŸÑÿ≠ŸÑ ÿßŸÑŸÖŸÜÿßÿ≥ÿ® ŸÑŸÖÿ¥ŸÉŸÑÿ™ŸÉ', en: 'See the best solution for your issue' } },
];

function App() {
  const [lang, setLang] = useState('ar');
  const [step, setStep] = useState(0);
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch {
      return null;
    }
  });
  const [problemText, setProblemText] = useState('');
  const [aiReply, setAiReply] = useState('');
  const [showSub, setShowSub] = useState(false);
  const [showAuth, setShowAuth] = useState(!user);

  const nextStep = () => setStep(s => Math.min(s + 1, steps.length - 1));
  const prevStep = () => setStep(s => Math.max(s - 1, 0));

  const handleAuthClose = () => {
    const u = JSON.parse(localStorage.getItem('user'));
    setUser(u);
    setShowAuth(!u);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setShowAuth(true);
  };

  const handleAskAI = async () => {
    setAiReply('');
    if (problemText.trim()) {
      setAiReply('...ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿ∞ŸÉŸä...');
      const reply = await getGeminiReply(problemText);
      setAiReply(reply || 'ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ≠ŸÑ ŸÖŸÜÿßÿ≥ÿ®');
    }
  };

  return (
    <div style={{ direction: lang === 'ar' ? 'rtl' : 'ltr', minHeight: '100vh' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1.5rem 2rem' }}>
        <h1 style={{ fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto', color: 'var(--primary)' }}>
          Smart Android Fix
        </h1>
        <div>
          <select value={lang} onChange={e => setLang(e.target.value)}>
            <option value="ar">ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</option>
            <option value="en">English</option>
          </select>
          {user && (
            <button className="btn-primary" style={{ marginRight: 16, marginLeft: 16 }} onClick={handleLogout}>
              {lang === 'ar' ? 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨' : 'Logout'}
            </button>
          )}
        </div>
      </header>
      <main style={{ maxWidth: 480, margin: '0 auto' }}>
        {user && (
          <div className="card">
            <h2>{steps[step].title[lang]}</h2>
            <p>{steps[step].desc[lang]}</p>
            {step === 1 && (
              <>
                <DeviceSelector lang={lang} />
                <textarea
                  style={{ width: '100%', marginTop: 12, minHeight: 60, fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto' }}
                  placeholder={lang === 'ar' ? 'ÿßŸÉÿ™ÿ® ŸàÿµŸÅ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ© ŸáŸÜÿß...' : 'Describe your problem...'}
                  value={problemText}
                  onChange={e => setProblemText(e.target.value)}
                />
                <button className="btn-primary" style={{ marginTop: 8 }} onClick={handleAskAI}>
                  {lang === 'ar' ? 'ÿßÿ≥ÿ£ŸÑ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä' : 'Ask AI'}
                </button>
                <div style={{ marginTop: 12, display: 'flex', alignItems: 'center', gap: 8 }}>
                  <button
                    className="btn-primary"
                    style={{ position: 'relative', opacity: 0.7, paddingLeft: 36 }}
                    onClick={() => setShowSub(true)}
                  >
                    <span style={{ position: 'absolute', left: 12, top: '50%', transform: 'translateY(-50%)', color: '#ffb300', fontSize: 20 }}>üîí</span>
                    {lang === 'ar' ? 'ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ŸÑŸÑŸÖÿ¥ŸÉŸÑÿ©' : 'Upload Problem Image'}
                  </button>
                </div>
              </>
            )}
            {step === 2 && (
              <div style={{ marginTop: 18 }}>
                <h3>{lang === 'ar' ? 'ÿ±ÿØ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä:' : 'AI Response:'}</h3>
                <div style={{ background: '#f7f9fc', borderRadius: 8, padding: 12, minHeight: 60 }}>
                  {aiReply || (lang === 'ar' ? 'ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ŸàÿµŸÅ ÿ®ÿπÿØ.' : 'No description sent yet.')}
                </div>
              </div>
            )}
            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 24 }}>
              <button className="btn-primary" onClick={prevStep} disabled={step === 0}>{lang === 'ar' ? 'ÿßŸÑÿ≥ÿßÿ®ŸÇ' : 'Back'}</button>
              <button className="btn-primary" onClick={nextStep} disabled={step === steps.length - 1}>{lang === 'ar' ? 'ÿßŸÑÿ™ÿßŸÑŸä' : 'Next'}</button>
            </div>
          </div>
        )}
      </main>
      {showAuth && <AuthPopup lang={lang} onClose={handleAuthClose} />}
      {showSub && <SubscriptionPopup lang={lang} onClose={() => setShowSub(false)} />}
    </div>
  );
}

export default App;
