import React, { useState } from 'react';

const brands = [
  { name: 'Samsung', ar: 'سامسونج', devices: [
    { model: 'Galaxy S23 Ultra', imei: '', serial: '' },
    { model: 'Galaxy A54', imei: '', serial: '' },
    { model: 'Galaxy M33', imei: '', serial: '' },
    { model: 'Galaxy Note 20', imei: '', serial: '' },
  ] },
  { name: 'Xiaomi', ar: 'شاومي', devices: [
    { model: 'Redmi Note 12', imei: '', serial: '' },
    { model: 'Poco X5 Pro', imei: '', serial: '' },
  ] },
  { name: 'Oppo', ar: 'اوبو', devices: [
    { model: 'Oppo Reno8', imei: '', serial: '' },
    { model: 'Oppo A77', imei: '', serial: '' },
  ] },
  { name: 'Huawei', ar: 'هواوي', devices: [
    { model: 'Huawei P50 Pro', imei: '', serial: '' },
    { model: 'Huawei Nova 10', imei: '', serial: '' },
  ] },
];

function DeviceSelector({ lang }) {
  const [search, setSearch] = useState('');
  const [selected, setSelected] = useState(null);

  const allDevices = brands.flatMap(b => b.devices.map(d => ({ ...d, brand: lang === 'ar' ? b.ar : b.name })));
  const filtered = allDevices.filter(d =>
    d.model.toLowerCase().includes(search.toLowerCase()) ||
    d.imei.includes(search) ||
    d.serial.includes(search)
  );

  return (
    <div>
      <input
        type="text"
        placeholder={lang === 'ar' ? 'ابحث باسم الجهاز أو IMEI أو السيريال' : 'Search by model, IMEI or serial'}
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ width: '100%' }}
      />
      <div style={{ maxHeight: 180, overflowY: 'auto', marginTop: 8 }}>
        {filtered.map((d, i) => (
          <div
            key={d.model + i}
            onClick={() => setSelected(d)}
            style={{
              padding: '0.7rem 1rem',
              background: selected === d ? 'var(--accent)' : '#f5f7fa',
              borderRadius: 8,
              marginBottom: 6,
              cursor: 'pointer',
              fontWeight: selected === d ? 'bold' : 'normal',
              fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto',
              display: 'flex',
              justifyContent: 'space-between',
            }}
          >
            <span>{d.brand}</span>
            <span>{d.model}</span>
          </div>
        ))}
        {filtered.length === 0 && <div style={{ color: '#888', padding: 12 }}>{lang === 'ar' ? 'لا يوجد نتائج' : 'No results found'}</div>}
      </div>
    </div>
  );
}

export default DeviceSelector;
