import React, { useState } from 'react';

// قائمة ضخمة من الأجهزة (مثال مختصر، يمكن توليدها تلقائياً)
const brands = [
  { name: 'Samsung', ar: 'سامسونج', devices: Array.from({length: 200}, (_,i) => ({ model: `Galaxy S${i+1}`, imei: '', serial: '' })) },
  { name: 'Xiaomi', ar: 'شاومي', devices: Array.from({length: 100}, (_,i) => ({ model: `Redmi ${i+1} Pro`, imei: '', serial: '' })) },
  { name: 'Oppo', ar: 'اوبو', devices: Array.from({length: 100}, (_,i) => ({ model: `Oppo Reno${i+1}`, imei: '', serial: '' })) },
  { name: 'Huawei', ar: 'هواوي', devices: Array.from({length: 100}, (_,i) => ({ model: `Huawei P${i+1}`, imei: '', serial: '' })) },
];

function DeviceSelector({ lang, onSelect }) {
  const [search, setSearch] = useState('');
  const [selected, setSelected] = useState(null);

  const allDevices = brands.flatMap(b => b.devices.map(d => ({ ...d, brand: lang === 'ar' ? b.ar : b.name })));
  const filtered = allDevices.filter(d =>
    d.model.toLowerCase().includes(search.toLowerCase()) ||
    d.imei.includes(search) ||
    d.serial.includes(search)
  );

  return (
    <div className="device-selector">
      <label style={{ fontWeight: 'bold', marginBottom: 6, display: 'block' }}>
        {lang === 'ar' ? 'اختر نوع الجهاز' : 'Select Device'}
      </label>
      <input
        type="text"
        placeholder={lang === 'ar' ? 'ابحث باسم الجهاز أو IMEI أو السيريال' : 'Search by model, IMEI or serial'}
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ width: '100%', marginBottom: 8 }}
      />
      <div style={{ maxHeight: 180, overflowY: 'auto', marginTop: 8 }}>
        {filtered.map((d, i) => (
          <div
            key={d.model + i}
            onClick={() => { setSelected(d); onSelect(d); }}
            className={selected === d ? 'device-item selected' : 'device-item'}
            style={{
              padding: '0.7rem 1rem',
              background: selected === d ? 'var(--accent)' : '#f5f7fa',
              borderRadius: 8,
              marginBottom: 6,
              cursor: 'pointer',
              fontWeight: selected === d ? 'bold' : 'normal',
              fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto',
              display: 'flex',
              justifyContent: 'space-between',
              boxShadow: selected === d ? '0 2px 8px #ffb30055' : 'none',
              transition: 'all 0.2s',
            }}
          >
            <span>{d.brand}</span>
            <span>{d.model}</span>
          </div>
        ))}
        {filtered.length === 0 && <div style={{ color: '#888', padding: 12 }}>{lang === 'ar' ? 'لا يوجد نتائج' : 'No results found'}</div>}
      </div>
    </div>
  );
}

export default DeviceSelector;
