import React, { useState } from 'react';

function AuthPopup({ lang, onClose }) {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ email: '', password: '', name: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = e => setForm(f => ({ ...f, [e.target.name]: e.target.value }));
  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/api/${isLogin ? 'login' : 'signup'}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error || 'Error');
      // يمكن هنا حفظ بيانات المستخدم في localStorage
      localStorage.setItem('user', JSON.stringify(data));
      onClose();
    } catch (err) {
      setError(lang === 'ar' ? 'بيانات غير صحيحة أو المستخدم موجود' : err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="popup">
      <div className="popup-content">
        <button style={{ float: 'right', background: 'none', border: 'none', fontSize: 22, cursor: 'pointer' }} onClick={onClose}>×</button>
        <h2 style={{ fontFamily: lang === 'ar' ? 'Cairo' : 'Roboto' }}>
          {isLogin ? (lang === 'ar' ? 'تسجيل الدخول' : 'Login') : (lang === 'ar' ? 'إنشاء حساب' : 'Sign Up')}
        </h2>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <input
              type="text"
              name="name"
              placeholder={lang === 'ar' ? 'الاسم' : 'Name'}
              value={form.name}
              onChange={handleChange}
              required
              style={{ width: '100%' }}
            />
          )}
          <input
            type="email"
            name="email"
            placeholder={lang === 'ar' ? 'البريد الإلكتروني' : 'Email'}
            value={form.email}
            onChange={handleChange}
            required
            style={{ width: '100%' }}
          />
          <input
            type="password"
            name="password"
            placeholder={lang === 'ar' ? 'كلمة المرور' : 'Password'}
            value={form.password}
            onChange={handleChange}
            required
            style={{ width: '100%' }}
          />
          <button className="btn-primary" type="submit" style={{ width: '100%', marginTop: 12 }} disabled={loading}>
            {loading ? (lang === 'ar' ? 'جاري المعالجة...' : 'Processing...') : isLogin ? (lang === 'ar' ? 'دخول' : 'Login') : (lang === 'ar' ? 'تسجيل' : 'Sign Up')}
          </button>
        </form>
        {error && <div style={{ color: 'red', marginTop: 8, textAlign: 'center' }}>{error}</div>}
        <div style={{ marginTop: 16, textAlign: 'center' }}>
          <button style={{ background: 'none', border: 'none', color: 'var(--primary)', cursor: 'pointer' }} onClick={() => setIsLogin(l => !l)}>
            {isLogin
              ? (lang === 'ar' ? 'ليس لديك حساب؟ أنشئ حساب' : 'No account? Sign up')
              : (lang === 'ar' ? 'لديك حساب؟ سجل الدخول' : 'Have an account? Login')}
          </button>
        </div>
      </div>
    </div>
  );
}

export default AuthPopup;
