import React, { useState } from 'react';
import DeviceSelector from './components/DeviceSelector';
import AndroidVersionSelector from './components/AndroidVersionSelector';
import AuthPopup from './components/AuthPopup';
import SubscriptionPopup from './components/SubscriptionPopup';

async function getGeminiReply(problemText) {
  const apiKey = 'AIzaSyC4M2gQOe6sPlcoZRPu8g38VWbvfKAXSsw';
  const url = `https://generativelanguage.googleapis.com/v1beta2/models/gemini-pro:generateContent?key=${apiKey}`;
  const res = await fetch(url, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({
      contents: [{ parts: [{ text: problemText }] }]
    })
  });
  const data = await res.json();
  return data.candidates?.[0]?.content?.parts?.[0]?.text || '';
}

const steps = [
  { title: { ar: 'ÿßÿ®ÿØÿ£ ÿßŸÑÿ™ÿ¥ÿÆŸäÿµ ÿßŸÑÿ∞ŸÉŸä', en: 'Start Smart Diagnosis' }, desc: { ar: 'ÿßÿÆÿ™ÿ± ÿ¨Ÿáÿßÿ≤ŸÉ ŸàÿßÿµÿØÿßÿ± ÿßŸÑÿ£ŸÜÿØÿ±ŸàŸäÿØ ŸàÿßŸÑŸÖÿ¥ŸÉŸÑÿ©', en: 'Select your device, Android version, and issue' } },
];

function App() {
  const [lang, setLang] = useState('ar');
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch {
      return null;
    }
  });
  const [showAuth, setShowAuth] = useState(!user);
  const [showSub, setShowSub] = useState(false);
  const [selectedDevice, setSelectedDevice] = useState(null);
  const [androidVersion, setAndroidVersion] = useState('');
  const [problemText, setProblemText] = useState('');
  const [aiReply, setAiReply] = useState('');
  const [loadingAI, setLoadingAI] = useState(false);

  const handleAuthClose = () => {
    const u = JSON.parse(localStorage.getItem('user'));
    setUser(u);
    setShowAuth(!u);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    setShowAuth(true);
  };

  const handleAskAI = async () => {
    setAiReply('');
    setLoadingAI(true);
    if (problemText.trim() && selectedDevice && androidVersion) {
      const prompt = `${selectedDevice.model} (${selectedDevice.brand}) - Android ${androidVersion}: ${problemText}`;
      const reply = await getGeminiReply(prompt);
      setAiReply(reply || (lang === 'ar' ? 'ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ≠ŸÑ ŸÖŸÜÿßÿ≥ÿ®.' : 'No suitable solution found.'));
    } else {
      setAiReply(lang === 'ar' ? 'Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ¨Ÿáÿßÿ≤ ŸàÿßŸÑÿ•ÿµÿØÿßÿ± ŸàŸÉÿ™ÿßÿ®ÿ© ÿßŸÑŸÖÿ¥ŸÉŸÑÿ©.' : 'Please select device, version, and describe the problem.');
    }
    setLoadingAI(false);
  };

  return (
    <div className="main-bg">
      <header className="header">
        <h1 className="site-title">Smart Android Fix</h1>
        <div>
          <select value={lang} onChange={e => setLang(e.target.value)} className="lang-select">
            <option value="ar">ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</option>
            <option value="en">English</option>
          </select>
          {user && (
            <button className="btn-primary logout-btn" onClick={handleLogout}>
              {lang === 'ar' ? 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨' : 'Logout'}
            </button>
          )}
        </div>
      </header>
      <main className="main-content">
        <div className="card animated-card">
          <h2>{steps[0].title[lang]}</h2>
          <p>{steps[0].desc[lang]}</p>
          <DeviceSelector lang={lang} onSelect={setSelectedDevice} />
          <AndroidVersionSelector lang={lang} value={androidVersion} onChange={setAndroidVersion} />
          <textarea
            className="problem-textarea"
            placeholder={lang === 'ar' ? 'ÿßŸÉÿ™ÿ® ŸàÿµŸÅ ÿßŸÑŸÖÿ¥ŸÉŸÑÿ© ŸáŸÜÿß...' : 'Describe your problem...'}
            value={problemText}
            onChange={e => setProblemText(e.target.value)}
          />
          <div className="actions-row">
            <button className="btn-primary" onClick={handleAskAI} disabled={loadingAI}>
              {loadingAI ? (lang === 'ar' ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ...' : 'Analyzing...') : (lang === 'ar' ? 'ÿßÿ≥ÿ£ŸÑ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä' : 'Ask AI')}
            </button>
            <button
              className="btn-primary locked-btn"
              onClick={() => setShowSub(true)}
            >
              <span className="lock-icon">üîí</span>
              {lang === 'ar' ? 'ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ŸÑŸÑŸÖÿ¥ŸÉŸÑÿ©' : 'Upload Problem Image'}
            </button>
          </div>
          {aiReply && (
            <div className="ai-reply animated-reply">
              <h3>{lang === 'ar' ? 'ÿ±ÿØ ÿßŸÑÿ∞ŸÉÿßÿ° ÿßŸÑÿßÿµÿ∑ŸÜÿßÿπŸä:' : 'AI Response:'}</h3>
              <div>{aiReply}</div>
            </div>
          )}
        </div>
      </main>
      {showAuth && <AuthPopup lang={lang} onClose={handleAuthClose} />}
      {showSub && <SubscriptionPopup lang={lang} onClose={() => setShowSub(false)} />}
    </div>
  );
}

export default App;
